const url = `${WEATHER_API_URL}/forecast?lat=2&lon=4&appid=${WEATHER_API_KEY}&units=metric`;

export const geoApiOptions = {
    method: 'GET',
    headers: {
        'X-RapidAPI-Key': 'cc6125055amsh2ecb17c487b91b9p19bdb8jsn3bebdc8cd3e1',
        'X-RapidAPI-Host': 'wft-geo-db.p.rapidapi.com'
    }
};

export const GEO_API_URL = "https://wft-geo-db.p.rapidapi.com/v1/geo";

export const WEATHER_API_URL = "https://api.openweathermap.org/data/2.5";
export const WEATHER_API_KEY = "79a734c4ecd42a8a4545de2baf30fd01";

const fetchWeatherData = async () => {
    try {
        const response = await fetch(url);
        
        if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        console.log(data);
        
    } catch (error) {
        console.error('Er is een fout opgetreden bij het ophalen van de weerdata:', error);
    }
};

fetchWeatherData();
